 <span class="token literal-property property">opacity</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
svg#freepik_stories<span class="token operator">-</span>programming<span class="token punctuation">.</span>animated #freepik<span class="token operator">--</span>Character<span class="token operator">--</span>inject<span class="token operator">-</span><span class="token number">2</span> <span class="token punctuation">{</span>
