<span class="token function">isGlob</span><span class="token punctuation">(</span><span class="token string">'abc/\\(aaa|bbb).js'</span><span class="token punctuation">,</span> <span class="token punctuation">{</span><span class="token literal-property property">strict</span><span class="token operator">:</span> <span class="token boolean">false</span><span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token function">isGlob</span><span class="token punctuation">(</span><span class="token string">'abc/\\[a-z].js'</span><span class="token punctuation">,</span> <span class="token punctuation">{</span><span class="token literal-property property">strict</span><span class="token operator">:</span> <span class="token boolean">false</span><span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token function">isGlob</span><span class="token punctuation">(</span><span class="token string">'abc/\\{a,b}.js'</span><span class="token punctuation">,</span> <span class="token punctuation">{</span><span class="token literal-property property">strict</span><span class="token operator">:</span> <span class="token boolean">false</span><span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token comment">//=> false</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="about" tabindex="-1"><a class="header-anchor" href="#about" aria-hidden="true">#</a> About</h2>
