<h3 id="built-in-types" tabindex="-1"><a class="header-anchor" href="#built-in-types" aria-hidden="true">#</a> Built-in types</h3>
<p>There are many advanced types most users don't know about.</p>
<ul>
<li>
<p><a href="https://www.typescriptlang.org/docs/handbook/utility-types.html#partialtype" target="_blank" rel="noopener noreferrer"><code v-pre>Partial&lt;T&gt;</code><ExternalLinkIcon/></a> - Make all properties in <code v-pre>T</code> optional.</p>
  <details>
  <summary>
  		Example
  </summary>
<p><a href="https://www.typescriptlang.org/play/#" target="_blank" rel="noopener noreferrer">Playground<ExternalLinkIcon/></a></p>
<div class="language-typescript ext-ts line-numbers-mode"><pre v-pre class="language-typescript"><code><span class="token keyword">interface</span> <span class="token class-name">NodeConfig</span> <span class="token punctuation">{</span>
		appName<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span>
		port<span class="token operator">:</span> <span class="token builtin">number</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token keyword">class</span> <span class="token class-name">NodeAppBuilder</span> <span class="token punctuation">{</span>
		<span class="token keyword">private</span> configuration<span class="token operator">:</span> NodeConfig <span class="token operator">=</span> <span class="token punctuation">{</span>
				appName<span class="token operator">:</span> <span class="token string">'NodeApp'</span><span class="token punctuation">,</span>
				port<span class="token operator">:</span> <span class="token number">3000</span>
		<span class="token punctuation">}</span><span class="token punctuation">;</span>

		<span class="token keyword">private</span> <span class="token generic-function"><span class="token function">updateConfig</span><span class="token generic class-name"><span class="token operator">&lt;</span>Key <span class="token keyword">extends</span> <span class="token keyword">keyof</span> NodeConfig<span class="token operator">></span></span></span><span class="token punctuation">(</span>key<span class="token operator">:</span> Key<span class="token punctuation">,</span> value<span class="token operator">:</span> NodeConfig<span class="token punctuation">[</span>Key<span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
				<span class="token keyword">this</span><span class="token punctuation">.</span>configuration<span class="token punctuation">[</span>key<span class="token punctuation">]</span> <span class="token operator">=</span> value<span class="token punctuation">;</span>
		<span class="token punctuation">}</span>

		<span class="token function">config</span><span class="token punctuation">(</span>config<span class="token operator">:</span> Partial<span class="token operator">&lt;</span>NodeConfig<span class="token operator">></span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
				<span class="token keyword">type</span> <span class="token class-name">NodeConfigKey</span> <span class="token operator">=</span> <span class="token keyword">keyof</span> NodeConfig<span class="token punctuation">;</span>

				<span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">const</span> key <span class="token keyword">of</span> Object<span class="token punctuation">.</span><span class="token function">keys</span><span class="token punctuation">(</span>config<span class="token punctuation">)</span> <span class="token keyword">as</span> NodeConfigKey<span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
						<span class="token keyword">const</span> updateValue <span class="token operator">=</span> config<span class="token punctuation">[</span>key<span class="token punctuation">]</span><span class="token punctuation">;</span>

						<span class="token keyword">if</span> <span class="token punctuation">(</span>updateValue <span class="token operator">===</span> <span class="token keyword">undefined</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
								<span class="token keyword">continue</span><span class="token punctuation">;</span>
						<span class="token punctuation">}</span>

						<span class="token keyword">this</span><span class="token punctuation">.</span><span class="token function">updateConfig</span><span class="token punctuation">(</span>key<span class="token punctuation">,</span> updateValue<span class="token punctuation">)</span><span class="token punctuation">;</span>
				<span class="token punctuation">}</span>

				<span class="token keyword">return</span> <span class="token keyword">this</span><span class="token punctuation">;</span>
		<span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token comment">// `Partial&lt;NodeConfig>`` allows us to provide only a part of the</span>
<span class="token comment">// NodeConfig interface.</span>
<span class="token keyword">new</span> <span class="token class-name">NodeAppBuilder</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">config</span><span class="token punctuation">(</span><span class="token punctuation">{</span>appName<span class="token operator">:</span> <span class="token string">'ToDoApp'</span><span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div>  </details>
</li>
<li>
<p><a href="https://www.typescriptlang.org/docs/handbook/utility-types.html#requiredtype" target="_blank" rel="noopener noreferrer"><code v-pre>Required&lt;T&gt;</code><ExternalLinkIcon/></a> - Make all properties in <code v-pre>T</code> required.</p>
  <details>
  <summary>
  		Example
  </summary>
<p><a href="https://typescript-play.js.org/?target=6#code/AQ4SwOwFwUwJwGYEMDGNgGED21VQGJZwC2wA3gFCjXAzFJgA2A-AFzADOUckA5gNxUaIYjA4ckvGG07c+g6gF8KQkAgCuEFFDA5O6gEbEwUbLm2ESwABQIixACJIoSdgCUYAR3Vg4MACYAPGYuFvYAfACU5Ko0APRxwADKMBD+wFAAFuh2Vv7OSBlYGdmc8ABu8LHKsRyGxqY4oQT21pTCIHQMjOwA5DAAHgACxAAOjDAAdChYxL0ANLHUouKSMH0AEmAAhJhY6ozpAJ77GTCMjMCiV0ToSAb7UJPPC9WRgrEJwAAqR6MwSRQPFGUFocDgRHYxnEfGAowh-zgUCOwF6KwkUl6tXqJhCeEsxDaS1AXSYfUGI3GUxmc0WSneQA" target="_blank" rel="noopener noreferrer">Playground<ExternalLinkIcon/></a></p>
<div class="language-typescript ext-ts line-numbers-mode"><pre v-pre class="language-typescript"><code><span class="token keyword">interface</span> <span class="token class-name">ContactForm</span> <span class="token punctuation">{</span>
		email<span class="token operator">?</span><span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span>
		message<span class="token operator">?</span><span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token keyword">function</span> <span class="token function">submitContactForm</span><span class="token punctuation">(</span>formData<span class="token operator">:</span> Required<span class="token operator">&lt;</span>ContactForm<span class="token operator">></span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
		<span class="token comment">// Send the form data to the server.</span>
<span class="token punctuation">}</span>

<span class="token function">submitContactForm</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
		email<span class="token operator">:</span> <span class="token string">'ex@mple.com'</span><span class="token punctuation">,</span>
		message<span class="token operator">:</span> <span class="token string">'Hi! Could you tell me more aboutâ€¦'</span><span class="token punctuation">,</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">// TypeScript error: missing property 'message'</span>
<span class="token function">submitContactForm</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
		email<span class="token operator">:</span> <span class="token string">'ex@mple.com'</span><span class="token punctuation">,</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div>  </details>
</li>
<li>
<p><a href="https://www.typescriptlang.org/docs/handbook/utility-types.html#readonlytype" target="_blank" rel="noopener noreferrer"><code v-pre>Readonly&lt;T&gt;</code><ExternalLinkIcon/></a> - Make all properties in <code v-pre>T</code> readonly.</p>
  <details>
  <summary>
  		Example
  </summary>
<p><a href="https://typescript-play.js.org/?target=6#" target="_blank" rel="noopener noreferrer">Playground<ExternalLinkIcon/></a></p>
<div class="language-typescript ext-ts line-numbers-mode"><pre v-pre class="language-typescript"><code><span class="token keyword">enum</span> LogLevel <span class="token punctuation">{</span>
		Off<span class="token punctuation">,</span>
		Debug<span class="token punctuation">,</span>
		Error<span class="token punctuation">,</span>
		Fatal
<span class="token punctuation">}</span><span class="token punctuation">;</span>

<span class="token keyword">interface</span> <span class="token class-name">LoggerConfig</span> <span class="token punctuation">{</span>
		name<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span>
		level<span class="token operator">:</span> LogLevel<span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token keyword">class</span> <span class="token class-name">Logger</span> <span class="token punctuation">{</span>
		config<span class="token operator">:</span> Readonly<span class="token operator">&lt;</span>LoggerConfig<span class="token operator">></span><span class="token punctuation">;</span>

		<span class="token function">constructor</span><span class="token punctuation">(</span><span class="token punctuation">{</span>name<span class="token punctuation">,</span> level<span class="token punctuation">}</span><span class="token operator">:</span> LoggerConfig<span class="token punctuation">)</span> <span class="token punctuation">{</span>
				<span class="token keyword">this</span><span class="token punctuation">.</span>config <span class="token operator">=</span> <span class="token punctuation">{</span>name<span class="token punctuation">,</span> level<span class="token punctuation">}</span><span class="token punctuation">;</span>
				Object<span class="token punctuation">.</span><span class="token function">freeze</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">.</span>config<span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">const</span> config<span class="token operator">:</span> LoggerConfig <span class="token operator">=</span> <span class="token punctuation">{</span>
	name<span class="token operator">:</span> <span class="token string">'MyApp'</span><span class="token punctuation">,</span>
	level<span class="token operator">:</span> LogLevel<span class="token punctuation">.</span>Debug
<span class="token punctuation">}</span><span class="token punctuation">;</span>

<span class="token keyword">const</span> logger <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Logger</span><span class="token punctuation">(</span>config<span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">// TypeScript Error: cannot assign to read-only property.</span>
logger<span class="token punctuation">.</span>config<span class="token punctuation">.</span>level <span class="token operator">=</span> LogLevel<span class="token punctuation">.</span>Error<span class="token punctuation">;</span>

<span class="token comment">// We are able to edit config variable as we please.</span>
