		intern<span class="token operator">:</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">,</span>
		developer<span class="token operator">:</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">,</span>
		<span class="token string-property property">'tech-lead'</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">,</span>
<span class="token punctuation">}</span><span class="token punctuation">;</span>

<span class="token comment">// Our team has decided to help John with his dream of becoming Software Developer.</span>
team<span class="token punctuation">.</span>intern<span class="token punctuation">.</span><span class="token function">push</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
	firstName<span class="token operator">:</span> <span class="token string">'John'</span><span class="token punctuation">,</span>
	lastName<span class="token operator">:</span> <span class="token string">'Doe'</span><span class="token punctuation">,</span>
