<span class="token keyword">function</span> <span class="token function">startServer</span><span class="token punctuation">(</span>handler<span class="token operator">:</span> RequestHandler<span class="token punctuation">,</span> config<span class="token operator">:</span> ServerConfig<span class="token punctuation">)</span><span class="token operator">:</span> <span class="token keyword">void</span> <span class="token punctuation">{</span>
	<span class="token keyword">const</span> server <span class="token operator">=</span> <span class="token keyword">require</span><span class="token punctuation">(</span><span class="token string">'http'</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">createServer</span><span class="token punctuation">(</span>handler<span class="token punctuation">)</span><span class="token punctuation">;</span>

	<span class="token keyword">const</span> port <span class="token operator">=</span> config<span class="token punctuation">.</span>port <span class="token operator">===</span> <span class="token keyword">null</span> <span class="token operator">?</span> <span class="token number">3000</span> <span class="token operator">:</span> <span class="token function">getPortValue</span><span class="token punctuation">(</span>config<span class="token punctuation">.</span>port<span class="token punctuation">)</span><span class="token punctuation">;</span>
	server<span class="token punctuation">.</span><span class="token function">listen</span><span class="token punctuation">(</span>port<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
