	Value <span class="token keyword">extends</span> Obj<span class="token punctuation">[</span>Key<span class="token punctuation">]</span>
<span class="token operator">></span></span></span> <span class="token punctuation">(</span>obj<span class="token operator">:</span> Obj<span class="token punctuation">,</span> key<span class="token operator">:</span> Key<span class="token punctuation">,</span> value<span class="token operator">:</span> Value<span class="token punctuation">)</span><span class="token operator">:</span> <span class="token keyword">void</span> <span class="token punctuation">{</span>
