import{_ as s,o as a,c as l,a as t,w as d,b as r,r as i,d as e}from"./app.19a0d412.js";var n="/assets/Server-Components_0.8d7457a8.png",o="/assets/Server-Components_1.666c001c.png";const h={},c=e('<h1 id="nextjs14\u110B\u1174-\u1109\u1165\u1107\u1165\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173-\u110B\u1175\u1112\u1162\u1112\u1161\u1100\u1175" tabindex="-1"><a class="header-anchor" href="#nextjs14\u110B\u1174-\u1109\u1165\u1107\u1165\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173-\u110B\u1175\u1112\u1162\u1112\u1161\u1100\u1175" aria-hidden="true">#</a> Nextjs14\uC758 \uC11C\uBC84\uCEF4\uD3EC\uB10C\uD2B8 \uC774\uD574\uD558\uAE30</h1><div class="custom-container tip"><p class="custom-container-title">\u{1F4A1}\uC774 \uD3EC\uC2A4\uD305\uC744 \uC77D\uC73C\uBA74</p><p>Nextjs\uC758 \uC11C\uBC84\uCEF4\uD3EC\uB10C\uD2B8\uC5D0 \uB300\uD574\uC11C \uC27D\uAC8C \uC774\uD574\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p></div><h1 id="\u1109\u1165\u1107\u1165-\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173" tabindex="-1"><a class="header-anchor" href="#\u1109\u1165\u1107\u1165-\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173" aria-hidden="true">#</a> \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8</h1><p>React \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC11C\uBC84\uC5D0\uC11C \uB80C\uB354\uB9C1\uB418\uACE0 \uC120\uD0DD\uC801\uC73C\uB85C \uCE90\uC2DC\uB420 \uC218 \uC788\uB294 UI\uB97C \uC791\uC131\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. Next.js\uC5D0\uC11C\uB294 \uC2A4\uD2B8\uB9AC\uBC0D \uBC0F \uBD80\uBD84 \uB80C\uB354\uB9C1\uC744 \uAC00\uB2A5\uD558\uAC8C \uD558\uAE30 \uC704\uD574 \uB80C\uB354\uB9C1 \uC791\uC5C5\uC774 \uACBD\uB85C \uC138\uADF8\uBA3C\uD2B8\uB85C \uB098\uB204\uC5B4\uC9C0\uBA70 \uC138 \uAC00\uC9C0 \uB2E4\uB978 \uC11C\uBC84 \uB80C\uB354\uB9C1 \uC804\uB7B5\uC774 \uC788\uC2B5\uB2C8\uB2E4.</p><ul><li>\uC815\uC801 \uB80C\uB354\uB9C1</li><li>\uB3D9\uC801 \uB80C\uB354\uB9C1</li><li>\uC2A4\uD2B8\uB9AC\uBC0D</li></ul><h2 id="\u1109\u1165\u1107\u1165-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC\u110B\u1174-\u110C\u1161\u11BC\u110C\u1165\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1109\u1165\u1107\u1165-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC\u110B\u1174-\u110C\u1161\u11BC\u110C\u1165\u11B7" aria-hidden="true">#</a> \uC11C\uBC84 \uB80C\uB354\uB9C1\uC758 \uC7A5\uC810</h2><p>\uC11C\uBC84\uC5D0\uC11C \uB80C\uB354\uB9C1 \uC791\uC5C5\uC744 \uC218\uD589\uD558\uB294 \uAC83\uC5D0\uB294 \uBA87 \uAC00\uC9C0 \uC774\uC810\uC774 \uC788\uC2B5\uB2C8\uB2E4.</p><ul><li><strong>\uB370\uC774\uD130 \uAC00\uC838\uC624\uAE30</strong>: \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD558\uBA74 \uB370\uC774\uD130 \uAC00\uC838\uC624\uAE30\uB97C \uC11C\uBC84\uB85C \uC774\uB3D9\uD558\uC5EC \uB370\uC774\uD130 \uC18C\uC2A4\uC5D0 \uAC00\uAE5D\uAC8C \uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 \uB80C\uB354\uB9C1\uC5D0 \uD544\uC694\uD55C \uB370\uC774\uD130\uB97C \uAC00\uC838\uC624\uB294 \uB370 \uAC78\uB9AC\uB294 \uC2DC\uAC04\uC744 \uC904\uC774\uACE0 \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uC218\uD589\uD574\uC57C \uD558\uB294 \uC694\uCCAD \uC218\uB97C \uC904\uC5EC \uC131\uB2A5\uC744 \uD5A5\uC0C1\uC2DC\uD0AC \uC218 \uC788\uC2B5\uB2C8\uB2E4.</li><li><strong>\uBCF4\uC548</strong>: \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD558\uBA74 \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0 \uBBFC\uAC10\uD55C \uB370\uC774\uD130\uC640 \uB85C\uC9C1\uC744 \uC720\uC9C0\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uD1A0\uD070 \uBC0F API \uD0A4\uB97C \uB178\uCD9C\uC2DC\uD0A4\uC9C0 \uC54A\uACE0 \uC11C\uBC84\uC5D0 \uBCF4\uAD00\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</li><li><strong>\uCE90\uC2F1</strong>: \uC11C\uBC84\uC5D0\uC11C \uB80C\uB354\uB9C1\uD558\uBA74 \uACB0\uACFC\uB97C \uCE90\uC2DC\uD558\uACE0 \uB2E4\uC74C \uC694\uCCAD \uBC0F \uC0AC\uC6A9\uC790 \uC804\uCCB4\uC5D0 \uAC78\uCCD0 \uC7AC\uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 \uAC01 \uC694\uCCAD\uB9C8\uB2E4 \uC218\uD589\uB418\uB294 \uB80C\uB354\uB9C1 \uBC0F \uB370\uC774\uD130 \uAC00\uC838\uC624\uAE30 \uC591\uC744 \uC904\uC5EC \uC131\uB2A5\uC744 \uD5A5\uC0C1\uC2DC\uD0A4\uACE0 \uBE44\uC6A9\uC744 \uC808\uAC10\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</li><li><strong>\uBC88\uB4E4 \uD06C\uAE30</strong>: \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC774\uC804\uC5D0 \uD074\uB77C\uC774\uC5B8\uD2B8 \uC790\uBC14\uC2A4\uD06C\uB9BD\uD2B8 \uBC88\uB4E4 \uD06C\uAE30\uC5D0 \uC601\uD5A5\uC744 \uBBF8\uCCE4\uC744 \uC218 \uC788\uB294 \uD070 \uC885\uC18D\uC131\uC744 \uC11C\uBC84\uC5D0 \uC720\uC9C0\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uAC83\uC740 \uD074\uB77C\uC774\uC5B8\uD2B8\uAC00 \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC704\uD55C JavaScript\uB97C \uB2E4\uC6B4\uB85C\uB4DC, \uAD6C\uBB38 \uBD84\uC11D \uBC0F \uC2E4\uD589\uD560 \uD544\uC694\uAC00 \uC5C6\uC73C\uBBC0\uB85C \uC778\uD130\uB137 \uC18D\uB3C4\uAC00 \uB290\uB9B0 \uC0AC\uC6A9\uC790\uB098 \uC131\uB2A5\uC774 \uB0AE\uC740 \uAE30\uAE30\uB97C \uC0AC\uC6A9\uD558\uB294 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uC774\uC810\uC774 \uC788\uC2B5\uB2C8\uB2E4.</li><li><strong>\uCD08\uAE30 \uD398\uC774\uC9C0 \uB85C\uB4DC \uBC0F \uCCAB \uBC88\uC9F8 \uCF58\uD150\uCE20 \uD45C\uC2DC (FCP)</strong> : \uC11C\uBC84\uC5D0\uC11C\uB294 \uD398\uC774\uC9C0\uB97C \uB80C\uB354\uB9C1\uD558\uB294 \uB370 \uD544\uC694\uD55C JavaScript\uB97C \uB2E4\uC6B4\uB85C\uB4DC, \uAD6C\uBB38 \uBD84\uC11D \uBC0F \uC2E4\uD589\uD558\uB294 \uB3D9\uC548 \uB300\uAE30\uD558\uC9C0 \uC54A\uACE0 \uC0AC\uC6A9\uC790\uAC00 \uD398\uC774\uC9C0\uB97C \uC989\uC2DC \uBCFC \uC218 \uC788\uB3C4\uB85D HTML\uC744 \uC0DD\uC131\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</li><li><strong>\uAC80\uC0C9 \uC5D4\uC9C4 \uCD5C\uC801\uD654 \uBC0F \uC18C\uC15C \uB124\uD2B8\uC6CC\uD06C \uACF5\uC720 \uAC00\uB2A5\uC131</strong>: \uB80C\uB354\uB9C1\uB41C HTML\uC744 \uAC80\uC0C9 \uC5D4\uC9C4 \uBD07\uC774 \uD398\uC774\uC9C0\uB97C \uC778\uB371\uC2F1\uD558\uACE0 \uC18C\uC15C \uB124\uD2B8\uC6CC\uD06C \uBD07\uC774 \uD398\uC774\uC9C0\uC5D0 \uB300\uD55C \uC18C\uC15C \uCE74\uB4DC \uBBF8\uB9AC\uBCF4\uAE30\uB97C \uC0DD\uC131\uD558\uB294 \uB370 \uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</li><li><strong>\uC2A4\uD2B8\uB9AC\uBC0D</strong>: \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD558\uBA74 \uB80C\uB354\uB9C1 \uC791\uC5C5\uC744 \uCCAD\uD06C\uB85C \uBD84\uD560\uD558\uC5EC \uC900\uBE44\uB418\uB294 \uB300\uB85C \uD074\uB77C\uC774\uC5B8\uD2B8\uB85C \uC2A4\uD2B8\uB9AC\uBC0D\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB97C \uD1B5\uD574 \uC0AC\uC6A9\uC790\uB294 \uC11C\uBC84\uC5D0\uC11C \uC804\uCCB4 \uD398\uC774\uC9C0\uB97C \uB80C\uB354\uB9C1\uD560 \uB54C\uAE4C\uC9C0 \uAE30\uB2E4\uB9B4 \uD544\uC694 \uC5C6\uC774 \uD398\uC774\uC9C0 \uC77C\uBD80\uB97C \uBA3C\uC800 \uBCFC \uC218 \uC788\uC2B5\uB2C8\uB2E4.</li></ul><h2 id="next-js\u110B\u1166\u1109\u1165-\u1109\u1165\u1107\u1165-\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173-\u1109\u1161\u110B\u116D\u11BC\u1112\u1161\u1100\u1175" tabindex="-1"><a class="header-anchor" href="#next-js\u110B\u1166\u1109\u1165-\u1109\u1165\u1107\u1165-\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173-\u1109\u1161\u110B\u116D\u11BC\u1112\u1161\u1100\u1175" aria-hidden="true">#</a> Next.js\uC5D0\uC11C \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8 \uC0AC\uC6A9\uD558\uAE30</h2><p>\uAE30\uBCF8\uC801\uC73C\uB85C Next.js\uB294 \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD569\uB2C8\uB2E4. \uC774\uB97C \uD1B5\uD574 \uCD94\uAC00 \uAD6C\uC131 \uC5C6\uC774 \uC11C\uBC84 \uB80C\uB354\uB9C1\uC744 \uC790\uB3D9\uC73C\uB85C \uAD6C\uD604\uD560 \uC218 \uC788\uC73C\uBA70 \uD544\uC694\uD560 \uB54C \uD074\uB77C\uC774\uC5B8\uD2B8 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC790\uC138\uD55C \uB0B4\uC6A9\uC740 <a href="">\uD074\uB77C\uC774\uC5B8\uD2B8 \uCEF4\uD3EC\uB10C\uD2B8</a>\uC5D0\uC11C \uD655\uC778\uD558\uC2E4 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="\u1109\u1165\u1107\u1165-\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173\u1100\u1161-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC\u1103\u116C\u1102\u1173\u11AB-\u1107\u1161\u11BC\u1107\u1165\u11B8\u110B\u1173\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1109\u1165\u1107\u1165-\u110F\u1165\u11B7\u1111\u1169\u1102\u1165\u11AB\u1110\u1173\u1100\u1161-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC\u1103\u116C\u1102\u1173\u11AB-\u1107\u1161\u11BC\u1107\u1165\u11B8\u110B\u1173\u11AB" aria-hidden="true">#</a> \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uAC00 \uB80C\uB354\uB9C1\uB418\uB294 \uBC29\uBC95\uC740?</h2><p>\uC11C\uBC84\uC5D0\uC11C Next.js\uB294 \uB80C\uB354\uB9C1\uC744 \uC870\uC815\uD558\uAE30 \uC704\uD574 React\uC758 API\uB97C \uC0AC\uC6A9\uD569\uB2C8\uB2E4. \uB80C\uB354\uB9C1 \uC791\uC5C5\uC740 \uB2E4\uC74C\uACFC \uAC19\uC740 \uB450 \uB2E8\uACC4\uB85C \uBD84\uD560\uB429\uB2C8\uB2E4: \uAC1C\uBCC4 \uACBD\uB85C \uC138\uADF8\uBA3C\uD2B8 \uBC0F Suspense Boundaries.</p><p>\uAC01 \uCCAD\uD06C\uB294 \uB2E4\uC74C\uACFC \uAC19\uC740 \uB450 \uB2E8\uACC4\uB85C \uB80C\uB354\uB9C1\uB429\uB2C8\uB2E4:</p><ul><li>React\uB294 \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uD2B9\uC218\uD55C \uB370\uC774\uD130 \uD615\uC2DD\uC778 React Server Component Payload (RSC Payload)\uB85C \uB80C\uB354\uB9C1\uD569\uB2C8\uB2E4.</li><li>Next.js\uB294 RSC Payload\uC640 \uD074\uB77C\uC774\uC5B8\uD2B8 \uCEF4\uD3EC\uB10C\uD2B8 JavaScript \uC9C0\uC2DC\uBB38\uC744 \uC0AC\uC6A9\uD558\uC5EC \uC11C\uBC84\uC5D0\uC11C HTML\uC744 \uB80C\uB354\uB9C1\uD569\uB2C8\uB2E4. \uADF8\uB7F0 \uB2E4\uC74C \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uC11C:</li><li>HTML\uC740 \uD398\uC774\uC9C0\uB97C \uC989\uC2DC \uBCFC \uC218 \uC788\uB3C4\uB85D \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uC758 \uBE60\uB978 \uBE44\uB300\uD654\uD615 \uBBF8\uB9AC\uBCF4\uAE30\uB85C \uC0AC\uC6A9\uB429\uB2C8\uB2E4. \uC774\uB294 \uCD08\uAE30 \uD398\uC774\uC9C0 \uB85C\uB4DC\uC5D0\uB9CC \uD574\uB2F9\uB429\uB2C8\uB2E4.</li><li>React \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8 \uD398\uC774\uB85C\uB4DC\uB294 \uD074\uB77C\uC774\uC5B8\uD2B8 \uBC0F \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8 \uD2B8\uB9AC\uB97C \uC870\uC815\uD558\uACE0 DOM\uC744 \uC5C5\uB370\uC774\uD2B8\uD558\uB294 \uB370 \uC0AC\uC6A9\uB429\uB2C8\uB2E4.</li><li>JavaScript \uC9C0\uC2DC\uBB38\uC740 \uD074\uB77C\uC774\uC5B8\uD2B8 \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC218\uD654\uD558\uACE0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uC0C1\uD638 \uC791\uC6A9 \uAC00\uB2A5\uD558\uAC8C \uB9CC\uB4DC\uB294 \uB370 \uC0AC\uC6A9\uB429\uB2C8\uB2E4. <blockquote><p>React \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8 \uD398\uC774\uB85C\uB4DC (RSC)\uB780 \uBB34\uC5C7\uC778\uAC00\uC694? RSC \uD398\uC774\uB85C\uB4DC\uB294 \uB80C\uB354\uB9C1\uB41C React \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8 \uD2B8\uB9AC\uC758 \uAC04\uACB0\uD55C \uBC14\uC774\uB108\uB9AC \uD45C\uD604\uC785\uB2C8\uB2E4. \uD074\uB77C\uC774\uC5B8\uD2B8\uC758 React\uC5D0\uC11C DOM\uC744 \uC5C5\uB370\uC774\uD2B8\uD558\uB294 \uB370 \uC0AC\uC6A9\uB429\uB2C8\uB2E4. RSC \uD398\uC774\uB85C\uB4DC\uC5D0\uB294 \uB2E4\uC74C\uC774 \uD3EC\uD568\uB429\uB2C8\uB2E4:</p></blockquote></li><li>\uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uC758 \uB80C\uB354\uB9C1\uB41C \uACB0\uACFC</li><li>\uD074\uB77C\uC774\uC5B8\uD2B8 \uCEF4\uD3EC\uB10C\uD2B8\uAC00 \uB80C\uB354\uB9C1\uB420 \uC704\uCE58\uC5D0 \uB300\uD55C \uC790\uB9AC \uD45C\uC2DC\uC790 \uBC0F \uADF8\uB4E4\uC758 JavaScript \uD30C\uC77C\uC5D0 \uB300\uD55C \uCC38\uC870</li><li>\uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uC5D0\uC11C \uD074\uB77C\uC774\uC5B8\uD2B8 \uCEF4\uD3EC\uB10C\uD2B8\uB85C \uC804\uB2EC\uB41C \uBAA8\uB4E0 props</li></ul><p><ins class="adsbygoogle" style="display:block;" data-ad-client="ca-pub-4877378276818686" data-ad-slot="9743150776" data-ad-format="auto" data-full-width-responsive="true"></ins></p>',15),p=e('<h2 id="\u1109\u1165\u1107\u1165-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC-\u110C\u1165\u11AB\u1105\u1163\u11A8" tabindex="-1"><a class="header-anchor" href="#\u1109\u1165\u1107\u1165-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC-\u110C\u1165\u11AB\u1105\u1163\u11A8" aria-hidden="true">#</a> \uC11C\uBC84 \uB80C\uB354\uB9C1 \uC804\uB7B5</h2><p>\uC11C\uBC84 \uB80C\uB354\uB9C1\uC5D0\uB294 \uC815\uC801, \uB3D9\uC801 \uBC0F \uC2A4\uD2B8\uB9AC\uBC0D\uC758 \uC138 \uAC00\uC9C0 \uD558\uC704 \uC9D1\uD569\uC774 \uC788\uC2B5\uB2C8\uB2E4.</p><h3 id="\u110C\u1165\u11BC\u110C\u1165\u11A8-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC-\u1100\u1175\u1107\u1169\u11AB\u1100\u1161\u11B9" tabindex="-1"><a class="header-anchor" href="#\u110C\u1165\u11BC\u110C\u1165\u11A8-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC-\u1100\u1175\u1107\u1169\u11AB\u1100\u1161\u11B9" aria-hidden="true">#</a> \uC815\uC801 \uB80C\uB354\uB9C1 (\uAE30\uBCF8\uAC12)</h3><p>\uC815\uC801 \uB80C\uB354\uB9C1\uC5D0\uC11C\uB294 \uACBD\uB85C\uAC00 \uBE4C\uB4DC \uC2DC\uAC04\uC5D0 \uB80C\uB354\uB9C1\uB418\uAC70\uB098 \uB370\uC774\uD130 \uC7AC\uC720\uD6A8\uD654 \uD6C4\uC5D0 \uBC31\uADF8\uB77C\uC6B4\uB4DC\uC5D0\uC11C \uB80C\uB354\uB9C1\uB429\uB2C8\uB2E4. \uACB0\uACFC\uB294 \uCE90\uC2DC\uB418\uC5B4 \uCF58\uD150\uCE20 \uC804\uC1A1 \uB124\uD2B8\uC6CC\uD06C (CDN)\uB85C \uD478\uC2DC\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC815\uC801 \uB80C\uB354\uB9C1\uC740 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uAC1C\uC778\uD654\uB418\uC9C0 \uC54A\uC740 \uB370\uC774\uD130\uAC00 \uC788\uB294 \uACBD\uB85C\uC5D0 \uC720\uC6A9\uD558\uBA70 \uBE4C\uB4DC \uC2DC\uAC04\uC5D0 \uC54C \uC218 \uC788\uB294 \uC815\uC801 \uBE14\uB85C\uADF8 \uAE00\uC774\uB098 \uC81C\uD488 \uD398\uC774\uC9C0\uC640 \uAC19\uC740 \uACBD\uC6B0\uC5D0 \uD574\uB2F9\uD569\uB2C8\uB2E4.</p><h3 id="\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC" aria-hidden="true">#</a> \uB3D9\uC801 \uB80C\uB354\uB9C1</h3><p>\uB3D9\uC801 \uB80C\uB354\uB9C1\uC5D0\uC11C\uB294 \uAC01 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uC694\uCCAD \uC2DC\uAC04\uC5D0 \uACBD\uB85C\uAC00 \uB80C\uB354\uB9C1\uB429\uB2C8\uB2E4. \uB3D9\uC801 \uB80C\uB354\uB9C1\uC740 \uC0AC\uC6A9\uC790\uC5D0\uAC8C \uAC1C\uC778\uD654\uB41C \uB370\uC774\uD130\uAC00 \uC788\uB294 \uACBD\uB85C \uB610\uB294 \uC694\uCCAD \uC2DC\uAC04\uC5D0\uB9CC \uC54C \uC218 \uC788\uB294 \uCFE0\uD0A4 \uB610\uB294 URL\uC758 \uAC80\uC0C9 \uB9E4\uAC1C\uBCC0\uC218\uC640 \uAC19\uC740 \uC815\uBCF4\uAC00 \uC788\uB294 \uACBD\uB85C\uC5D0 \uC720\uC6A9\uD569\uB2C8\uB2E4.</p><blockquote><p>\uCE90\uC2DC\uB41C \uB370\uC774\uD130\uB97C \uC0AC\uC6A9\uD55C \uB3D9\uC801 \uB77C\uC6B0\uD2B8 \uB300\uBD80\uBD84\uC758 \uC6F9 \uC0AC\uC774\uD2B8\uC5D0\uC11C \uB77C\uC6B0\uD2B8\uB294 \uC644\uC804\uD788 \uC815\uC801\uC774\uAC70\uB098 \uC644\uC804\uD788 \uB3D9\uC801\uC774 \uC544\uB2D9\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uCE90\uC2DC\uB41C \uC81C\uD488 \uB370\uC774\uD130\uB97C \uC0AC\uC6A9\uD558\uACE0 \uC77C\uC815 \uAC04\uACA9\uC73C\uB85C \uC7AC\uAC80\uC99D\uB418\uB294 \uB3D9\uC548 \uCE90\uC2DC\uB418\uC9C0 \uC54A\uC740 \uAC1C\uC778\uD654\uB41C \uACE0\uAC1D \uB370\uC774\uD130\uAC00 \uC788\uB294 \uC804\uC790 \uC0C1\uAC70\uB798 \uD398\uC774\uC9C0\uAC00 \uC788\uC744 \uC218 \uC788\uC2B5\uB2C8\uB2E4. Next.js\uC5D0\uC11C\uB294 \uCE90\uC2DC\uB41C \uBC0F \uCE90\uC2DC\uB418\uC9C0 \uC54A\uC740 \uB370\uC774\uD130\uB97C \uBAA8\uB450 \uC0AC\uC6A9\uD558\uB294 \uB3D9\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uB418\uB294 \uACBD\uB85C\uB97C \uAC00\uC9C8 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 RSC \uD398\uC774\uB85C\uB4DC\uC640 \uB370\uC774\uD130\uAC00 \uBCC4\uB3C4\uB85C \uCE90\uC2DC\uB418\uAE30 \uB54C\uBB38\uC5D0 \uB3D9\uC801 \uB80C\uB354\uB9C1\uC744 \uC120\uD0DD\uD560 \uB54C \uC694\uCCAD \uC2DC\uAC04\uC5D0 \uBAA8\uB4E0 \uB370\uC774\uD130\uB97C \uAC00\uC838\uC624\uB294 \uAC83\uC758 \uC131\uB2A5\uC801\uC778 \uC601\uD5A5\uC5D0 \uB300\uD574 \uAC71\uC815\uD560 \uD544\uC694\uAC00 \uC5C6\uC74C\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4. \uC804\uCCB4 \uACBD\uB85C \uCE90\uC2DC \uBC0F \uB370\uC774\uD130 \uCE90\uC2DC\uC5D0 \uB300\uD574 \uC790\uC138\uD788 \uC54C\uC544\uBCF4\uC138\uC694.</p></blockquote><h4 id="\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1112\u1161\u11B7\u1109\u116E" tabindex="-1"><a class="header-anchor" href="#\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1112\u1161\u11B7\u1109\u116E" aria-hidden="true">#</a> \uB3D9\uC801 \uD568\uC218</h4><p>\uB3D9\uC801 \uD568\uC218\uB294 \uC694\uCCAD \uC2DC\uAC04\uC5D0\uB9CC \uC54C \uC218 \uC788\uB294 \uC815\uBCF4\uB97C \uAE30\uBC18\uC73C\uB85C \uD569\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uC0AC\uC6A9\uC790\uC758 \uCFE0\uD0A4, \uD604\uC7AC \uC694\uCCAD \uD5E4\uB354 \uB610\uB294 URL\uC758 \uAC80\uC0C9 \uB9E4\uAC1C\uBCC0\uC218\uC785\uB2C8\uB2E4. Next.js\uC5D0\uC11C \uC774\uB7EC\uD55C \uB3D9\uC801 \uD568\uC218\uB294 \uB2E4\uC74C\uACFC \uAC19\uC2B5\uB2C8\uB2E4:</p><ul><li>cookies() \uBC0F headers(): \uC11C\uBC84 \uCEF4\uD3EC\uB10C\uD2B8\uC5D0\uC11C \uC774\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC804\uCCB4 \uACBD\uB85C\uAC00 \uC694\uCCAD \uC2DC\uAC04\uC5D0 \uB3D9\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uB429\uB2C8\uB2E4.</li><li>searchParams: Pages \uD504\uB86D\uC744 \uC0AC\uC6A9\uD558\uBA74 \uD398\uC774\uC9C0\uAC00 \uC694\uCCAD \uC2DC\uAC04\uC5D0 \uB3D9\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uB429\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uD568\uC218\uB97C \uC0AC\uC6A9\uD558\uBA74 \uC804\uCCB4 \uACBD\uB85C\uAC00 \uC694\uCCAD \uC2DC\uAC04\uC5D0 \uB3D9\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uB429\uB2C8\uB2E4.</li></ul><h4 id="\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC\u110B\u1173\u1105\u1169-\u110C\u1165\u11AB\u1112\u116A\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1103\u1169\u11BC\u110C\u1165\u11A8-\u1105\u1166\u11AB\u1103\u1165\u1105\u1175\u11BC\u110B\u1173\u1105\u1169-\u110C\u1165\u11AB\u1112\u116A\u11AB" aria-hidden="true">#</a> \uB3D9\uC801 \uB80C\uB354\uB9C1\uC73C\uB85C \uC804\uD658</h4><p>\uB80C\uB354\uB9C1 \uC911\uC5D0 \uB3D9\uC801 \uD568\uC218 \uB610\uB294 \uCE90\uC2DC\uB418\uC9C0 \uC54A\uC740 \uB370\uC774\uD130 \uC694\uCCAD\uC774 \uBC1C\uACAC\uB418\uBA74 Next.js\uB294 \uC804\uCCB4 \uACBD\uB85C\uB97C \uB3D9\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uD558\uB3C4\uB85D \uC804\uD658\uD569\uB2C8\uB2E4. \uB2E4\uC74C \uD45C\uB294 \uB3D9\uC801 \uD568\uC218\uC640 \uB370\uC774\uD130 \uCE90\uC2F1\uC774 \uACBD\uB85C\uB97C \uC815\uC801\uC73C\uB85C \uB610\uB294 \uB3D9\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uD558\uB294 \uB370 \uC5B4\uB5BB\uAC8C \uC601\uD5A5\uC744 \uBBF8\uCE58\uB294\uC9C0\uB97C \uC694\uC57D\uD569\uB2C8\uB2E4:</p><table><thead><tr><th>Dynamic Functions</th><th>Data Cached</th><th>Rendering Strategy</th></tr></thead><tbody><tr><td>No</td><td>Cached</td><td>Statically Rendered</td></tr><tr><td>Yes</td><td>Cached</td><td>Dynamically Rendered</td></tr><tr><td>No</td><td>Not Cached</td><td>Dynamically Rendered</td></tr><tr><td>Yes</td><td>Not Cached</td><td>Dynamically Rendered</td></tr></tbody></table><p>\uC704 \uD45C\uC5D0\uC11C \uACBD\uB85C\uAC00 \uC644\uC804\uD788 \uC815\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uB418\uB824\uBA74 \uBAA8\uB4E0 \uB370\uC774\uD130\uAC00 \uCE90\uC2DC\uB418\uC5B4\uC57C \uD569\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uB3D9\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uB418\uB294 \uACBD\uB85C\uB294 \uCE90\uC2DC\uB41C \uBC0F \uCE90\uC2DC\uB418\uC9C0 \uC54A\uC740 \uB370\uC774\uD130 \uAC00\uC838\uC624\uAE30\uB97C \uBAA8\uB450 \uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 RSC \uD398\uC774\uB85C\uB4DC\uC640 \uB370\uC774\uD130\uAC00 \uBCC4\uB3C4\uB85C \uCE90\uC2DC\uB418\uAE30 \uB54C\uBB38\uC5D0 \uC694\uCCAD \uC2DC\uAC04\uC5D0 \uBAA8\uB4E0 \uB370\uC774\uD130\uB97C \uAC00\uC838\uC624\uB294 \uAC83\uC758 \uC131\uB2A5\uC5D0 \uB300\uD574 \uAC71\uC815\uD560 \uD544\uC694\uAC00 \uC5C6\uC74C\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4. \uAC1C\uBC1C\uC790\uB85C\uC11C \uC815\uC801 \uBC0F \uB3D9\uC801 \uB80C\uB354\uB9C1 \uC911\uC5D0\uC11C \uC120\uD0DD\uD560 \uD544\uC694\uB294 \uC5C6\uC73C\uBA70 Next.js\uAC00 \uC0AC\uC6A9\uB418\uB294 \uAE30\uB2A5\uACFC API\uC5D0 \uB530\uB77C \uAC01 \uACBD\uB85C\uC5D0 \uB300\uD574 \uC790\uB3D9\uC73C\uB85C \uCD5C\uC801\uC758 \uB80C\uB354\uB9C1 \uC804\uB7B5\uC744 \uC120\uD0DD\uD569\uB2C8\uB2E4. \uB300\uC2E0 \uD2B9\uC815 \uB370\uC774\uD130\uB97C \uCE90\uC2DC\uD558\uAC70\uB098 \uC7AC\uAC80\uC99D\uD558\uB294 \uC2DC\uC810\uC744 \uC120\uD0DD\uD558\uACE0 UI\uC758 \uC77C\uBD80\uB97C \uC2A4\uD2B8\uB9AC\uBC0D\uD560\uC9C0 \uC5EC\uBD80\uB97C \uC120\uD0DD\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p><ins class="adsbygoogle" style="display:block;" data-ad-client="ca-pub-4877378276818686" data-ad-slot="9743150776" data-ad-format="auto" data-full-width-responsive="true"></ins></p>',15),u=e('<h3 id="\u1109\u1173\u1110\u1173\u1105\u1175\u1106\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1109\u1173\u1110\u1173\u1105\u1175\u1106\u1175\u11BC" aria-hidden="true">#</a> \uC2A4\uD2B8\uB9AC\uBC0D</h3><img src="'+n+'"><p>\uC2A4\uD2B8\uB9AC\uBC0D\uC744 \uC0AC\uC6A9\uD558\uBA74 \uC11C\uBC84\uC5D0\uC11C UI\uB97C \uC810\uC9C4\uC801\uC73C\uB85C \uB80C\uB354\uB9C1\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC791\uC5C5\uC740 \uCCAD\uD06C\uB85C \uBD84\uD560\uB418\uC5B4 \uC900\uBE44\uB418\uB294 \uB300\uB85C \uD074\uB77C\uC774\uC5B8\uD2B8\uB85C \uC2A4\uD2B8\uB9AC\uBC0D\uB418\uC5B4 \uC0AC\uC6A9\uC790\uAC00 \uC804\uCCB4 \uCF58\uD150\uCE20 \uB80C\uB354\uB9C1\uC774 \uC644\uB8CC\uB420 \uB54C\uAE4C\uC9C0 \uAE30\uB2E4\uB9AC\uC9C0 \uC54A\uACE0\uB3C4 \uD398\uC774\uC9C0\uC758 \uC77C\uBD80\uB97C \uBCFC \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><img src="'+o+'"><p>\uC2A4\uD2B8\uB9AC\uBC0D\uC740 Next.js App Router\uC5D0 \uAE30\uBCF8\uC73C\uB85C \uB0B4\uC7A5\uB418\uC5B4 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 \uCD08\uAE30 \uD398\uC774\uC9C0 \uB85C\uB4DC \uC131\uB2A5 \uBC0F \uC804\uCCB4 \uACBD\uB85C\uB97C \uB80C\uB354\uB9C1\uD558\uB294 \uB370 \uBE14\uB85D\uB418\uB294 \uB290\uB9B0 \uB370\uC774\uD130 \uAC00\uC838\uC624\uAE30\uC5D0 \uB530\uB77C UI\uB97C \uAC1C\uC120\uD558\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB429\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4 \uC81C\uD488 \uD398\uC774\uC9C0\uC758 \uB9AC\uBDF0\uC785\uB2C8\uB2E4. \uB85C\uB529.js \uBC0F React Suspense\uB97C \uC0AC\uC6A9\uD558\uC5EC \uACBD\uB85C \uC138\uADF8\uBA3C\uD2B8\uB97C \uC2A4\uD2B8\uB9AC\uBC0D\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uC790\uC138\uD55C \uB0B4\uC6A9\uC740 <a href="">\uB85C\uB529 UI</a> \uBC0F <a href="">\uC2A4\uD2B8\uB9AC\uBC0D \uC139\uC158</a>\uC744 \uCC38\uC870\uD558\uC138\uC694.</p>',6);function x(g,b){return a(),l("div",null,[c,(a(),t(i("script"),null,{default:d(()=>[r(" (adsbygoogle = window.adsbygoogle || []).push({}); ")]),_:1})),p,(a(),t(i("script"),null,{default:d(()=>[r(" (adsbygoogle = window.adsbygoogle || []).push({}); ")]),_:1})),u])}var _=s(h,[["render",x],["__file","index.html.vue"]]);export{_ as default};
